CREATE TABLE IF NOT EXISTS `raw_data` (
  `rid` int(11) NOT NULL AUTO_INCREMENT,
  `chinese` text,
  `portuguese` text,
  `bilingual` text,
  `status` varchar(255) NOT NULL,
  PRIMARY KEY (`rid`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=11 ;

