CREATE TABLE IF NOT EXISTS `sentence` (
  `sid` int(11) NOT NULL AUTO_INCREMENT,
  `rid` int(11) NOT NULL,
  `chinese` mediumtext NOT NULL,
  `portuguese` mediumtext NOT NULL,
  PRIMARY KEY (`sid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

